import requests

from bs4 import BeautifulSoup

from urllib.parse import urlparse

from axiestudio.custom import Component

from axiestudio.inputs import MessageTextInput

from axiestudio.template import Output

from axiestudio.schema import Data

class SubpagesExtractorComponent(Component):

    display_name = "Subpages Extractor"

    description = "Acessa um site e retorna todas as subpáginas do mesmo domínio."

    icon = "web-crawler"

    inputs = [

        MessageTextInput(

            name="url",

            display_name="URL do Site",

            info="Insira a URL do site para extrair as subpáginas.",

        ),

    ]

    outputs = [

        Output(display_name="Subpáginas Encontradas", name="subpages_list", method="extract_subpages"),

    ]

    def extract_subpages(self) -> Data:

        url = self.url

        domain = urlparse(url).netloc

        subpages = set()

        try:

            response = requests.get(url)

            soup = BeautifulSoup(response.content, "html.parser")

            for link in soup.find_all("a"):

                href = link.get("href")

                if href and domain in href:

                    subpages.add(href)

            return Data(data={"subpages": list(subpages)})

        except Exception as e:

            return Data(data={"error": str(e)})

