from langchain_core.output_parsers import StrOutputParser
from langchain_core.prompts import ChatPromptTemplate
from langchain_core.pydantic_v1 import BaseModel
from langchain_core.runnables import RunnablePassthrough
from axiestudio.base.agents.agent import LCToolsAgentComponent
from langchain_core.runnables.base import RunnableSequence

class RewriteRetrieveRead(LCToolsAgentComponent):
    display_name = "Retireval Rewriter Chain"
    description = "The Retrieval Rewriter Chain is a methodology that rewrites user queries while consulting external tools, optimizing them for more accurate and relevant results by refining the input based on specific parameters and conditions."
    documentation: str = "https://github.com/langchain-ai/langchain/tree/master/templates/rewrite-retrieve-read"
    icon = "LangChain"
    name = "Retrieve Rewriter Chain"

    inputs = LCToolsAgentComponent._base_inputs + [
        HandleInput(name="llm", display_name="Language Model", input_types=["LanguageModel"], required=True, info="An LLM Model Object (It can be found in any LLM Component)."),
        MultilineInput(name="prompt_system", display_name="Prompt System Retireval", value="Answer the users question based only on the following context:\n<context>\n{context}\n</context>\n\nQuestion: {question}\n", info="A prompt template to structure the answer."),
        MultilineInput(name="prompt_system_revisor", display_name="Prompt System Revisor", value="Improve my query making it better for searching. End the query with '**'. Question: {x} Answer:", info="A refine querying that intends to improve the user query."),
    ]

    outputs = [
        Output(display_name="Text", name="output_text", method="build_chain_ans"),
        Output(display_name="Chain", name="output_chain", method="build_chain"),
    ]

    def build_chain_ans(self) -> Message:
        template = self.prompt_system
        prompt = ChatPromptTemplate.from_template(template)

        model = self.llm

        def retriever(query):
            retrieval_info = []
            for tool in self.tools:
                retrieval_info.append(tool.run(query))
            return str(retrieval_info)

        template = self.prompt_system_revisor
        rewrite_prompt = ChatPromptTemplate.from_template(template)

        chain = (
            {"context": retriever, "question": RunnablePassthrough()}
            | prompt
            | model
            | StrOutputParser()
        )

        def _parse(text):
            return text.strip("**")


        rewriter = rewrite_prompt | self.llm | StrOutputParser() | _parse

        rewrite_retrieve_read_chain = (
            {
                "context": {"x": RunnablePassthrough()} | rewriter | retriever,
                "question": RunnablePassthrough(),
            }
            | prompt
            | model
            | StrOutputParser()
        )


        output = rewrite_retrieve_read_chain.invoke(self.input_value)

        return Message(text = output)

    def build_chain(self) -> RunnableSequence:
        template = self.prompt_system
        prompt = ChatPromptTemplate.from_template(template)

        model = self.llm

        def retriever(query):
            retrieval_info = []
            for tool in self.tools:
                retrieval_info.append(tool.run(query))
            return str(retrieval_info)

        template = self.prompt_system_revisor
        rewrite_prompt = ChatPromptTemplate.from_template(template)

        chain = (
            {"context": retriever, "question": RunnablePassthrough()}
            | prompt
            | model
            | StrOutputParser()
        )

        def _parse(text):
            return text.strip("**")


        rewriter = rewrite_prompt | self.llm | StrOutputParser() | _parse

        rewrite_retrieve_read_chain = (
            {
                "context": {"x": RunnablePassthrough()} | rewriter | retriever,
                "question": RunnablePassthrough(),
            }
            | prompt
            | model
            | StrOutputParser()
        )

        self.status = str(rewrite_retrieve_read_chain)
        return rewrite_retrieve_read_chain

