import os

from typing import Union

from langchain_core.tools import Tool

from axiestudio.base.langchain_utilities.model import LCToolComponent
from axiestudio.inputs import SecretStrInput, MultilineInput, IntInput
from axiestudio.schema import Data

class GoogleSearchAPIComponent(LCToolComponent):
    display_name = "Tavily Search API"
    description = "Call Tavily Search API."
    name = "TavilySearchAPI"
    icon = "search-check"

    inputs = [
        SecretStrInput(name="tavily_api_key", display_name="Tavily API Key", required=True),
        MultilineInput(
            name="input_value",
            display_name="Input",
        )
    ]

    def run_model(self) -> Union[Data, list[Data]]:
        wrapper = self._build_wrapper()
        results = wrapper.results(query=self.input_value)
        data = [Data(data=result, text=result["snippet"]) for result in results]
        self.status = data
        return data

    def build_tool(self) -> Tool:
        wrapper = self._build_wrapper()
        return Tool(
            name="tavily_search",
            description="Search internet for recent results.",
            func=wrapper.run,
        )

    def _build_wrapper(self):
        try:
            from langchain_community.utilities.tavily_search import TavilySearchAPIWrapper
            from langchain_community.tools.tavily_search.tool import TavilySearchResults
            os.environ["TAVILY_API_KEY"] = self.tavily_api_key
        except ImportError:
            raise ImportError("Please install langchain-google-community to use GoogleSearchAPIWrapper.")
        return TavilySearchResults(api_wrapper=TavilySearchAPIWrapper())

