              "role": "user",
              "content": [
                {
                  "type": "text",
                  "text": prompt
                },
                {
                  "type": "image_url",
                  "image_url": {
                    "url": f"data:image/jpeg;base64,{b64_contents}"
                  }
                }
              ]
            }
          ],
          "max_tokens": MaxTokens
        }

        response = requests.post("https://api.openai.com/v1/chat/completions", headers=headers, json=payload)
