from axiestudio import CustomComponent


class DuckDuckGoSearchRunComponent(CustomComponent):
    display_name = "DuckDuckGoSearchRun"
    description = "A wrapper around DuckDuckGo Search. Useful for when you need to answer questions about current events. Input should be a search query."
    documentation = "https://api.python.langchain.com/en/latest/utilities/langchain_community.utilities.duckduckgo_search.DuckDuckGoSearchAPIWrapper.html"

    def build(
        self,
        api_wrapper: Optional[DuckDuckGoSearchAPIWrapper] = {}

