from axiestudio import CustomComponent
from axiestudio.field_typing import Data
import requests

class FetchHTMLContent(CustomComponent):
    display_name: str = "Fetch HTML"
    description: str = "Fetches HTML content from a specified URL."

    def build_config(self):
        return {"url": {"input_types": ["Data", "str"]}} 

    def build(self, url: str) -> Data:
        response = requests.get(url)
        self.status = str(response) + "\n\n" + response.text
        return response.text

