        MessageTextInput(name="filter_value3", display_name="Valor do Filtro para Terceira Coluna", value="", info="Valor para a terceira coluna"),
        MessageTextInput(name="filter_column4", display_name="Quarta Coluna para Filtro", value="", info="Nome da quarta coluna para aplicar o filtro"),
        MessageTextInput(name="filter_value4", display_name="Valor do Filtro para Quarta Coluna", value="", info="Valor para a quarta coluna"),

        # Inputs para filtros específicos (ano e intervalo)
        MessageTextInput(name="filter_column_year", display_name="Coluna de Ano", value="ano", info="Nome da coluna de ano para comparar"),
        MessageTextInput(name="year_value", display_name="Valor do Ano", value="", info="Ano que deseja verificar se está no intervalo"),
        MessageTextInput(name="filter_column_start", display_name="Data de Início", value="data_aplicacao_inicial", info="Coluna de data inicial do intervalo"),
        MessageTextInput(name="filter_column_end", display_name="Data de Fim", value="data_aplicacao_final", info="Coluna de data final do intervalo"),

        BoolInput(name="case_sensitive", display_name="Filtro Case Sensitive?", value=False, info="Ative para busca que diferencia maiúsculas e minúsculas"),
        MessageTextInput(name="foreign_table", display_name="Tabela Estrangeira para Consulta", value="", info="Nome da tabela estrangeira e chave")
    ]

    # Definir o output com o resultado da operação
    outputs = [
        Output(display_name="Resultado Dados", name="result", method="execute_operation")
    ]

    def execute_operation(self) -> Data:
        # Pegando os valores dos inputs
        supabase_url = self.supabase_url
        supabase_key = self.supabase_key
        table_name = self.table_name
        operation = self.operation
        record_data = self.record_data
        record_id = self.record_id
        columns = self.columns
        year_value = self.year_value  # Ano que queremos comparar
        filter_column_year = self.filter_column_year
        filter_column_start = self.filter_column_start
        filter_column_end = self.filter_column_end

        # Pegando os filtros múltiplos
        filter_column1 = self.filter_column1
        filter_value1 = self.filter_value1
        filter_column2 = self.filter_column2
        filter_value2 = self.filter_value2
        filter_column3 = self.filter_column3
        filter_value3 = self.filter_value3
        filter_column4 = self.filter_column4
        filter_value4 = self.filter_value4

        # Headers para a requisição
        headers = {
            "apikey": supabase_key,
            "Content-Type": "application/json"
        }

        # Base URL para a tabela
        url = f"{supabase_url}/rest/v1/{table_name}"

        # Operação de leitura com suporte a diferentes tipos de consultas
        if operation == "read":
            # Construindo a URL de leitura
            read_url = f"{url}?select={columns}"

            # Adicionando filtro de intervalo (ano entre duas datas)
            if year_value and filter_column_start and filter_column_end:
                # Verifica se o ano está entre data_aplicacao_inicial e data_aplicacao_final
                read_url += f"&{filter_column_start}=lte.{year_value}&{filter_column_end}=gte.{year_value}"

            # Adicionando filtros, se as colunas e valores estiverem definidos
            if filter_column1 and filter_value1:
                read_url += f"&{filter_column1}=eq.{filter_value1}"
            if filter_column2 and filter_value2:
                read_url += f"&{filter_column2}=eq.{filter_value2}"
            if filter_column3 and filter_value3:
                read_url += f"&{filter_column3}=eq.{filter_value3}"
            if filter_column4 and filter_value4:
                read_url += f"&{filter_column4}=eq.{filter_value4}"

            try:
                response = httpx.get(read_url, headers=headers)
                response.raise_for_status()  # Verifica se há erros HTTP
                data = response.json()
                return Data(value=data)
            except httpx.HTTPStatusError as e:
                return Data(value={"error": f"Erro ao conectar ao Supabase: {e.response.status_code} - {e.response.text}"})

        # Operação de criação
        elif operation == "create":
            try:
                data_to_create = json.loads(record_data)  # Transformar o JSON de entrada em dicionário
                response = httpx.post(url, headers=headers, json=data_to_create)
                response.raise_for_status()  # Verifica se há erros HTTP
                return Data(value={"success": "Registro criado com sucesso", "data": response.json()})
            except httpx.HTTPStatusError as e:
                return Data(value={"error": f"Erro ao conectar ao Supabase: {e.response.status_code} - {e.response.text}"})
            except json.JSONDecodeError:
                return Data(value={"error": "Erro ao processar os dados de entrada (JSON inválido)"})

        # Operação de atualização
        elif operation == "update":
            if not record_id:
                return Data(value={"error": "O campo ID do registro é obrigatório para atualizar"})
            update_url = f"{url}?id=eq.{record_id}"
            try:
                data_to_update = json.loads(record_data)  # Transformar o JSON de entrada em dicionário
                response = httpx.patch(update_url, headers=headers, json=data_to_update)
                response.raise_for_status()  # Verifica se há erros HTTP
                return Data(value={"success": "Registro atualizado com sucesso", "data": response.json()})
            except httpx.HTTPStatusError as e:
                return Data(value={"error": f"Erro ao conectar ao Supabase: {e.response.status_code} - {e.response.text}"})
            except json.JSONDecodeError:
                return Data(value={"error": "Erro ao processar os dados de entrada (JSON inválido)"})

        # Operação de exclusão
        elif operation == "delete":
            if not record_id:
                return Data(value={"error": "O campo ID do registro é obrigatório para deletar"})
            delete_url = f"{url}?id=eq.{record_id}"
            try:
                response = httpx.delete(delete_url, headers=headers)
                response.raise_for_status()  # Verifica se há erros HTTP
                return Data(value={"success": "Registro deletado com sucesso"})
            except httpx.HTTPStatusError as e:
                return Data(value={"error": f"Erro ao conectar ao Supabase: {e.response.status_code} - {e.response.text}"})

        else:
            return Data(value={"error": "Operação inválida"})

