            if year_value and filter_column_start and filter_column_end:
                # Verifica se o ano está entre data_aplicacao_inicial e data_aplicacao_final
                read_url += f"&{filter_column_start}=lte.{year_value}&{filter_column_end}=gte.{year_value}"

            # Adicionando filtros, se as colunas e valores estiverem definidos
            if filter_column1 and filter_value1:
                read_url += f"&{filter_column1}=eq.{filter_value1}"
            if filter_column2 and filter_value2:
                read_url += f"&{filter_column2}=eq.{filter_value2}"
            if filter_column3 and filter_value3:
                read_url += f"&{filter_column3}=eq.{filter_value3}"
            if filter_column4 and filter_value4:
                read_url += f"&{filter_column4}=eq.{filter_value4}"

            try:
                response = httpx.get(read_url, headers=headers)
                response.raise_for_status()  # Verifica se há erros HTTP
                data = response.json()
                return Data(value=data)
            except httpx.HTTPStatusError as e:
                return Data(value={"error": f"Erro ao conectar ao Supabase: {e.response.status_code} - {e.response.text}"})

        # Operação de criação
        elif operation == "create":
            try:
                data_to_create = json.loads(record_data)  # Transformar o JSON de entrada em dicionário
                response = httpx.post(url, headers=headers, json=data_to_create)
                response.raise_for_status()  # Verifica se há erros HTTP
                return Data(value={"success": "Registro criado com sucesso", "data": response.json()})
            except httpx.HTTPStatusError as e:
                return Data(value={"error": f"Erro ao conectar ao Supabase: {e.response.status_code} - {e.response.text}"})
            except json.JSONDecodeError:
                return Data(value={"error": "Erro ao processar os dados de entrada (JSON inválido)"})

        # Operação de atualização
        elif operation == "update":
            if not record_id:
                return Data(value={"error": "O campo ID do registro é obrigatório para atualizar"})
            update_url = f"{url}?id=eq.{record_id}"
            try:
                data_to_update = json.loads(record_data)  # Transformar o JSON de entrada em dicionário
                response = httpx.patch(update_url, headers=headers, json=data_to_update)
                response.raise_for_status()  # Verifica se há erros HTTP
                return Data(value={"success": "Registro atualizado com sucesso", "data": response.json()})
            except httpx.HTTPStatusError as e:
                return Data(value={"error": f"Erro ao conectar ao Supabase: {e.response.status_code} - {e.response.text}"})
            except json.JSONDecodeError:
                return Data(value={"error": "Erro ao processar os dados de entrada (JSON inválido)"})

        # Operação de exclusão
        elif operation == "delete":
            if not record_id:
                return Data(value={"error": "O campo ID do registro é obrigatório para deletar"})
            delete_url = f"{url}?id=eq.{record_id}"
            try:
                response = httpx.delete(delete_url, headers=headers)
                response.raise_for_status()  # Verifica se há erros HTTP
                return Data(value={"success": "Registro deletado com sucesso"})
            except httpx.HTTPStatusError as e:
                return Data(value={"error": f"Erro ao conectar ao Supabase: {e.response.status_code} - {e.response.text}"})

        else:
            return Data(value={"error": "Operação inválida"})

import asyncio
import json
from typing import Any, List, Optional
from urllib.parse import parse_qsl, urlencode, urlparse, urlunparse

import httpx
from loguru import logger

from axiestudio.base.curl.parse import parse_context
from axiestudio.custom import Component
from axiestudio.io import DataInput, DropdownInput, IntInput, MessageTextInput, NestedDictInput, Output
from axiestudio.schema import Data
from axiestudio.schema.dotdict import dotdict


class APIRequestComponent(Component):
    display_name = "API Request"
    description = (
        "This component allows you to make HTTP requests to one or more URLs. "
        "You can provide headers and body as either dictionaries or Data objects. "
        "Additionally, you can append query parameters to the URLs.\n\n"
        "**Note:** Check advanced options for more settings."
    )
    icon = "Globe"
    name = "APIRequest"

    inputs = [
        MessageTextInput(
            name="urls",
            display_name="URLs",
            is_list=True,
            info="Enter one or more URLs, separated by commas.",
        ),
        MessageTextInput(
            name="curl",
            display_name="Curl",
            info="Paste a curl command to populate the fields. This will fill in the dictionary fields for headers and body.",
            advanced=False,
            refresh_button=True,
        ),
        DropdownInput(
            name="method",
            display_name="Method",
            options=["GET", "POST", "PATCH", "PUT"],
            value="GET",
            info="The HTTP method to use (GET, POST, PATCH, PUT).",
        ),
        NestedDictInput(
            name="headers",
            display_name="Headers",
            info="The headers to send with the request as a dictionary. This is populated when using the CURL field.",
            input_types=["Data"],
        ),
        NestedDictInput(
            name="body",
            display_name="Body",
            info="The body to send with the request as a dictionary (for POST, PATCH, PUT). This is populated when using the CURL field.",
            input_types=["Data"],
        ),
        DataInput(
            name="query_params",
            display_name="Query Parameters",
            info="The query parameters to append to the URL.",
        ),
        IntInput(
            name="timeout",
            display_name="Timeout",
            value=5,
            info="The timeout to use for the request.",
        ),
    ]

    outputs = [
        Output(display_name="Data", name="data", method="make_requests"),
    ]

    def parse_curl(self, curl: str, build_config: dotdict) -> dotdict:
        try:
            parsed = parse_context(curl)
            build_config["urls"]["value"] = [parsed.url]
            build_config["method"]["value"] = parsed.method.upper()
            build_config["headers"]["value"] = dict(parsed.headers)

            if parsed.data:
                try:
                    json_data = json.loads(parsed.data)
                    build_config["body"]["value"] = json_data
                except json.JSONDecodeError as e:
                    logger.error(f"Error decoding JSON data: {e}")
            else:
                build_config["body"]["value"] = {}
        except Exception as exc:
            logger.error(f"Error parsing curl: {exc}")
            raise ValueError(f"Error parsing curl: {exc}")
        return build_config

    def update_build_config(self, build_config: dotdict, field_value: Any, field_name: str | None = None):
        if field_name == "curl" and field_value:
            build_config = self.parse_curl(field_value, build_config)
        return build_config

    async def make_request(
        self,
        client: httpx.AsyncClient,
        method: str,
        url: str,
        headers: Optional[dict] = None,
        body: Optional[dict] = None,
        timeout: int = 5,
    ) -> Data:
        method = method.upper()
        if method not in ["GET", "POST", "PATCH", "PUT", "DELETE"]:
            raise ValueError(f"Unsupported method: {method}")

        if isinstance(body, str) and body:
            try:
                body = json.loads(body)
            except Exception as e:
                logger.error(f"Error decoding JSON data: {e}")
                body = None
                raise ValueError(f"Error decoding JSON data: {e}")

        data = body if body else None

        try:
            response = await client.request(method, url, headers=headers, json=data, timeout=timeout)
            try:
                result = response.json()
            except Exception:
                result = response.text
            return Data(
                data={
                    "source": url,
                    "headers": headers,
                    "status_code": response.status_code,
                    "result": result,
                },
            )
        except httpx.TimeoutException:
            return Data(
                data={
                    "source": url,
                    "headers": headers,
                    "status_code": 408,
                    "error": "Request timed out",
                },
            )
        except Exception as exc:
            return Data(
                data={
                    "source": url,
                    "headers": headers,
                    "status_code": 500,
                    "error": str(exc),
                },
            )

    def add_query_params(self, url: str, params: dict) -> str:
        url_parts = list(urlparse(url))
        query = dict(parse_qsl(url_parts[4]))
        query.update(params)
        url_parts[4] = urlencode(query)
        return urlunparse(url_parts)

    async def make_requests(self) -> List[Data]:
        method = self.method
        urls = [url.strip() for url in self.urls if url.strip()]
        curl = self.curl
        headers = self.headers or {}
        body = self.body or {}
        timeout = self.timeout
        query_params = self.query_params.data if self.query_params else {}

        if curl:
            self._build_config = self.parse_curl(curl, dotdict())

        if isinstance(headers, Data):
            headers = headers.data

        if isinstance(body, Data):
            body = body.data

        bodies = [body] * len(urls)

        urls = [self.add_query_params(url, query_params) for url in urls]

        async with httpx.AsyncClient() as client:
            results = await asyncio.gather(
                *[self.make_request(client, method, u, headers, rec, timeout) for u, rec in zip(urls, bodies)]
            )
        self.status = results
        return results

import json

from axiestudio.custom import Component
from axiestudio.io import MultilineInput, Output
from axiestudio.schema import Data


class WebhookComponent(Component):
    display_name = "Webhook Input"
    description = "Defines a webhook input for the flow."
    name = "Webhook"

    inputs = [
        MultilineInput(
            name="data",
            display_name="Data",
            info="Use this field to quickly test the webhook component by providing a JSON payload.",
        )
    ]
    outputs = [
        Output(display_name="Data", name="output_data", method="build_data"),
    ]

    def build_data(self) -> Data:
        message: str | Data = ""
        if not self.data:
            self.status = "No data provided."
            return Data(data={})
        try:
            body = json.loads(self.data or "{}")
        except json.JSONDecodeError:
            body = {"payload": self.data}
            message = f"Invalid JSON payload. Please check the format.\n\n{self.data}"
        data = Data(data=body)
        if not message:
            message = data
        self.status = message
        return data
