from axiestudio import CustomComponent
from typing import Dict
from pinecone import Pinecone
from axiestudio.schema import Data

class PineconeNamespacesComponent(CustomComponent):
    display_name = "Pinecone Namespaces Lister"
    description = "Lista todos os namespaces de um índice específico no Pinecone."
    documentation = "https://docs.pinecone.io/reference/describe_index_stats"
    icon = "pinecone"
    color = "#87CEEB"
    beta = False
    version = "1.0"
    name = "PineconeNamespacesComponent"

    def build_config(self):
        return {
            "api_key": {
                "display_name": "API Key",
                "description": "Sua chave de API do Pinecone",
                "type": "str",
                "required": True,
            },
            "environment": {
                "display_name": "Environment",
                "description": "Seu ambiente Pinecone",
                "type": "str",
                "required": True,
            },
            "index_name": {
                "display_name": "Index Name",
                "description": "Nome do índice Pinecone",
                "type": "str",
                "default": "documentossalvos",
                "required": True,
            },
        }

    def build(
        self,
        api_key: str,
        environment: str,
        index_name: str,
        **kwargs
    ) -> Data:
        try:
            # Inicializa o Pinecone com a nova API
            pc = Pinecone(
                api_key=api_key,
                environment=environment
            )
            # Conecta ao índice
            index = pc.Index(index_name)
            # Obtém estatísticas do índice
            stats = index.describe_index_stats()
            # Processa os namespaces
            namespaces = list(stats.namespaces.keys())
            result = {
                "status": "success",
                "namespaces": namespaces,
                "total_namespaces": len(namespaces),
                "namespace_stats": {
                    ns: {
                        "vector_count": stats.namespaces[ns].vector_count
                    } for ns in namespaces
                }
            }
            # Retorna como Data para compatibilidade com Langflow
            return Data(
                text=str(result),
                data=result
            )
            
        except Exception as e:
            error_result = {
                "status": "error",
                "message": str(e),
                "namespaces": [],
                "total_namespaces": 0
            }
            return Data(
                text=str(error_result),
                data=error_result
            )

