# from axiestudio.field_typing import Data
from axiestudio.schema import Record
from axiestudio.interface.custom.custom_component import CustomComponent
import requests
from requests.auth import HTTPBasicAuth
import json

class Component(CustomComponent):
    display_name = "Jira Cloud - POST comment on issue"
    description = "Add a comment on a target issue, on a Jira Cloud instance"
    documentation: str = "https://developer.atlassian.com/cloud/jira/platform/rest/v3/api-group-issue-comments/#api-rest-api-3-issue-issueidorkey-comment-post"
    icon = "custom_components"
    field_order = ["user_email", "api_token", "instance_subdomain", "issue_key"]

    def build_config(self):

        # return {"param": {"display_name": "Parameter"}}

        return {
            "issue_key": {
                "display_name": "Issue Key",
                "info": "Target issue to receive the comment"
            },
            "instance_subdomain": {
                "display_name": "Instance Subdomain",
                "info": "Subdomain of the instance"
            },
            "user_email": {
                "display_name": "User Email",
                "info": "Email address of the user commenting"
            },

            "api_token": {
                "display_name": "Api Token",
                "info": "Commenter user's API Token",
                "password": True
            },
            "comment_body": {
                "display_name": "Comment Body",
                "info": "Comment text"
            }
        }



    def build(self, issue_key: str, instance_subdomain: str, api_token: str, user_email: str, comment_body: str) -> Text:

        url = f"https://{instance_subdomain}.atlassian.net/rest/api/3/issue/{issue_key}/comment"

        auth = HTTPBasicAuth(user_email, api_token)
        headers = {
        "Accept": "application/json",
        "Content-Type": "application/json"
        }

        payload = json.dumps({
            "body": {
                "content": [
                    {
                        "content": [
                            {
                                "text": f"{comment_body}",
                                "type": "text"
                            }
                        ],
                        "type": "paragraph"
                    }
                ],
                "type": "doc",
                "version": 1
            },
        })

        response = requests.request(
        "POST",
        url,
        data=payload,
        headers=headers,
        auth=auth
        )

        api_response = response.json()
        return api_response

