from typing import List
from axiestudio import CustomComponent
from axiestudio.field_typing import Document

class RecursiveCharacterSplitterWithMetadata(CustomComponent):
    display_name: str = "RecursiveCharacterSplitterWithMetadata"
    description: str = "Split documents into chunks with overlapping content."

    field_config = {
        "documents": {
            "display_name": "Documents",
            "field_type": "List[Document]",
            "required": True,
        },
        "chunk_size": {
            "display_name": "Chunk Size",
            "field_type": "int",
            "required": True,
            "value": 1000,
        },
        "chunk_overlap": {
            "display_name": "Chunk Overlap",
            "field_type": "int",
            "required": True,
            "value": 200,
        },
        "separator": {
            "display_name": "Separator",
            "field_type": "str",
            "required": True,
            "value": "\n",
        }
    }

    def build_config(self):
        return self.field_config

    def _get_field_order(self):
        return ["documents", "chunk_size", "chunk_overlap", "separator"]

    def build(self, documents: List[Document], chunk_size: int, chunk_overlap: int, separator: str) -> List[Document]:
        def split(document: Document) -> List[Document]:
            chunks = []
            content = document.page_content
            metadata = document.metadata
            start = 0
            while start < len(content):
                end = start + chunk_size
                chunk = content[start:end]
                if end < len(content):
                    chunk = chunk.rsplit(separator, 1)[0]
                chunks.append(Document(page_content=chunk, metadata=metadata))
                start += chunk_size - chunk_overlap
            return chunks

        results = []
        for document in documents:
            results.extend(split(document))
        return results

    def input(self, documents: List[Document]) -> List[Document]:
        return self.build(
            documents,
            self.field_config['chunk_size']['value'],
            self.field_config['chunk_overlap']['value'],
            self.field_config['separator']['value']
        )

