from typing import Optional
from axiestudio.base.langchain_utilities.model import LCToolComponent
from axiestudio.inputs import MessageTextInput, StrInput, DictInput, SecretStrInput
from axiestudio.schema import Data
from axiestudio.field_typing import Tool
from langchain.tools import StructuredTool
from pydantic import BaseModel, Field

# NOTE: This component requires you to install the scrapegraphai, scrapegraphai[burr] (Use 'Pip Install' component from store)
#       and the playwright. I'm not sure on how to properly install Playwright for this case because I'm using the one I already
#       have installed on my machine for e2e testing. I _think_ `pip install playwright && playwright install` should work.
#       You may also install the playwright dependencies with playwright install-deps. Works on docker containers.

class ScrapeGraphAIComponent(LCToolComponent):
    display_name: str = "ScrapeGraphAI Scraper"
    description: str = "Scrape web content using ScrapeGraphAI _SmartScraperGraph_, single-page scrapper.\n\n Requires installation of scrapegraphai and playwright. Read more on [documentation](https://github.com/ScrapeGraphAI/Scrapegraph-ai).\n\n Could not make the tool output work. Help is appreciated."
    name = "ScrapeGraphScrapper"
    documentation: str = "https://github.com/scrapegraph/scrapegraphai"
    icon='🕷️'
    beta = True

    inputs = [
        SecretStrInput(
            name="openai_api_key",
            display_name="OpenAI API Key",
            info="Your OpenAI API key for the LLM.",
        ),
        MessageTextInput(
            name="prompt",
            display_name="Scraping Prompt",
            info="The prompt describing what information to extract.",
        ),
        MessageTextInput(
            name="source",
            display_name="Source URL",
            info="The URL of the webpage to scrape.",
        ),
        StrInput(
            name="model",
            display_name="LLM Model",
            info="The OpenAI model to use (e.g., 'gpt-4', 'gpt-3.5-turbo').",
        ),
        DictInput(
            name="additional_config",
            display_name="Additional Configuration",
            info="Additional configuration options for ScrapeGraphAI.",
            advanced=True,
             is_list=True,
        ),
    ]

    class ScrapeGraphAISchema(BaseModel):
        prompt: str = Field(..., description="The prompt describing what information to extract")
        source: str = Field(..., description="The URL of the webpage to scrape")
        model: str = Field(default="gpt-3.5-turbo", description="The OpenAI model to use")
        additional_config: Optional[dict] = Field(default=None, description="Additional configuration options")

    def run_model(self) -> Data:
        try:
            from scrapegraphai.graphs import SmartScraperGraph
        except ImportError:
            raise ImportError(
                "Could not import scrapegraphai package. "
                "Please install it with `pip install scrapegraphai`."
            )

        graph_config = {
            "llm": {
                "api_key": self.openai_api_key,
                "model": self.model,
            },
            "verbose": True,
            "headless": True,
        }

        if self.additional_config:
            graph_config.update(self.additional_config)

        smart_scraper_graph = SmartScraperGraph(
            prompt=self.prompt,
            source=self.source,
            config=graph_config
        )

        result = smart_scraper_graph.run()
        data = Data(data=result)
        self.status = data
        return data

    def build_tool(self) -> Tool:
        return StructuredTool.from_function(
            name="ScrapeGraphScrapper",
            description="Scrape web content using ScrapeGraphAI. Input should be a dictionary with 'prompt', 'source', optional 'model', and optional 'additionalConfig'.",
            func=self._scrape_with_scrapegraphai,
            args_schema=self.ScrapeGraphAISchema,
        )

    def _scrape_with_scrapegraphai(self, prompt: str, source: str, model: str = "gpt-3.5-turbo", additionalConfig: Optional[dict] = None) -> dict:
        try:
            from scrapegraphai.graphs import SmartScraperGraph
        except ImportError:
            raise ImportError(
                "Could not import scrapegraphai package. "
                "Please install it with `pip install scrapegraphai`."
            )

        graph_config = {
            "llm": {
                "api_key": self.openai_api_key,
                "model": model,
            },
            "verbose": True,
            "headless": True,
        }

        if additionalConfig:
            graph_config.update(additionalConfig)

        smart_scraper_graph = SmartScraperGraph(
            prompt=prompt,
            source=source,
            config=graph_config
        )

        return smart_scraper_graph.run()

