class DiscordTextMessageListener(CustomComponent):
    display_name = "DiscordTextMessageListener"
    description = "Listen the input from a discord channel (webhook)"
    field_order = ["token", "channel_id"]
    icon = "Discord"

    def build_config(self) -> dict:
        return {
            "token": {
                "display_name": "Token",
                "advanced": False,
                "password": True,
                "required": True,
            }, "channel_id": {
                "display_name": "ChannelId",
                "advanced": False,
                "required": True,
            }
        }

    def build(self, token: str, channel_id: int) -> str:
        import requests
        resp = requests.post("http://discord:7880/api/v1/listen_message", json={"token": token, "channel_id": channel_id})
        if resp.status_code == 200:
            return resp.content
        else:
            raise Exception("Timeout exception")

