def get_weather(
        city_name,
) -> str:
    """
    Get the current weather for `city_name`
    """

    if not isinstance(city_name, str):
        raise TypeError("City name must be a string")

    key_selection = {
        "current_condition": ["temp_C", "FeelsLikeC", "humidity", "weatherDesc", "observation_time"],
    }
    import requests
    try:
        print("city_name->", city_name)
        url = "https://wttr.in/{city_name}?format=j1"
        print("url->", url)
        resp = requests.get(f"https://wttr.in/{city_name}?format=j1",proxies={"http": "http://10.1.1.12::8118", "https": "http://10.1.1.12:8118"})
        resp.raise_for_status()
        resp = resp.json()
        ret = {k: {_v: resp[k][0][_v] for _v in v}
               for k, v in key_selection.items()}
    except:
        import traceback
        ret = "Error encountered while fetching weather data!\n" + traceback.format_exc()

    return str(ret)

