            "code": {
                "advanced": True,
            },
        }

    def build(
        self,
        google_api_key: str,
        model: str,
        max_output_tokens: Optional[int] = None,
        temperature: float = 0.1,
        top_k: Optional[int] = None,
        top_p: Optional[float] = None,
        n: Optional[int] = 1,
