from langchain_community.utilities.dalle_image_generator import DallEAPIWrapper
from axiestudio import CustomComponent
from typing import Optional, Any
from openai import OpenAI
from langchain.schema import BasePromptTemplate
from axiestudio.field_typing import RangeSpec
import json
from langchain_community.tools.json.tool import JsonSpec
from langchain.schema import Document

class DallEImageGenerator(CustomComponent):
    display_name: str = "(Not Chain) DALL¬∑E Image Generator"
    description: str = "Generates new images based on text prompts, edits existing images, or creates variations of an image.(Not Chain)"
    icon = "üñåÔ∏èÔ∏è"

    def build_config(self):
        return {
            "openai_api_key": {
                "display_name": "OpenAI API Key",
                "info": "OpenAI API key for authentication.",
                "required": True,
                "password": True,
            },
            "prompt": {
                "info": "A text description of the desired image(s). The maximum length is 1000 characters for dall-e-2 and 4000 characters for dall-e-3.",
                "required": True,
            },
            "model": {
                "options": ["dall-e-2", "dall-e-3"],
                "value": "dall-e-2",
                "info": "The model to use for image generation.",
            },
            "n": {
                "info": "The number of images to generate. Must be between 1 and 10. For dall-e-3, only n=1 is supported.",
                "default": 1,
                "range_spec": RangeSpec(min=1, max=10, step=1),
            },
            "quality": {
                "options": ["standard", "hd"],
                "default": "standard",
                "info": "The quality of the image that will be generated. This parameter is only supported for dall-e-3.",
            },
            "response_format": {
                "options": ["url", "b64_json"],
                "default": "url",
                "info": "The format in which the generated images are returned.",
            },
            "size": {
                "options": ["256x256", "512x512", "1024x1024", "1792x1024", "1024x1792"],
                "default": "1024x1024",
                "info": "The size of the generated images. Varies based on the selected model.",
            },
            "style": {
                "options": ["vivid", "natural"],
                "default": "vivid",
                "info": "The style of the generated images. This parameter is only supported for dall-e-3.",
            },
        }

    def build(self, openai_api_key: str, prompt: str, model: str, n: float, quality: str, response_format: str, size: str, style: Optional[str] = None) -> Document:

        openai = OpenAI(api_key=openai_api_key)



        if model == "dall-e-3":
            if n != 1:
                raise ValueError("For dall-e-3, only n=1 is supported.")
            if size not in ["1024x1024", "1792x1024", "1024x1792"]:
                raise ValueError(
                    "For dall-e-3, supported sizes are 1024x1024, 1792x1024, or 1024x1792.")
            if quality not in ["standard", "hd"]:
                raise ValueError(
                    "For dall-e-3, quality must be either 'standard' or 'hd'.")
            if style not in [None, "vivid", "natural"]:
                raise ValueError(
                    "For dall-e-3, style must be either 'vivid', 'natural', or None.")

            response = openai.images.generate(
                
                model=model,
                prompt=prompt,
                n=int(n),
                size=size,
                quality=quality,
                response_format=response_format,
                style=style,
            )
        else:  # dall-e-2
            if size not in ["256x256", "512x512", "1024x1024"]:
                raise ValueError(
                    "For dall-e-2, supported sizes are 256x256, 512x512, or 1024x1024.")
            if n < 1 or n > 10:
                raise ValueError("n must be between 1 and 10 for dall-e-2.")

            response = openai.images.generate(

                model=model,
                prompt=prompt,
                n=int(n),
                size=size,
                response_format=response_format,
            )


        return response  # Îã§Î•∏ Ìè¨Îß∑ Î∞òÌôò (Ïòà: b64_json)

