from axiestudio.custom import Component
from axiestudio.io import MessageTextInput, SecretStrInput, DropdownInput, Output
from axiestudio.schema.message import Message
from openai import OpenAI

class OpenAIPreviewModel(Component):
    display_name = "OpenAI Preview Model"
    description = "Generates text using OpenAI's preview language models (o1 series)."
    icon = "brain"
    
    inputs = [
        SecretStrInput(
            name="api_key",
            display_name="API Key",
            info="Your OpenAI API key",
        ),
        MessageTextInput(
            name="user_message",
            display_name="User Message",
            info="The message to send to the model",
        ),
        DropdownInput(
            name="model_name",
            display_name="Model Name",
            info="The name of the OpenAI model to use",
            options=[
                "o1-preview",
                "o1-mini",
            ],
            value="o1-preview",
        ),
    ]
    
    outputs = [
        Output(
            name="generated_text",
            display_name="Generated Text",
            method="generate_text",
        ),
    ]
    
    def generate_text(self) -> Message:
        try:
            client = OpenAI(api_key=self.api_key)
            response = client.chat.completions.create(
                model=self.model_name,
                messages=[
                    {
                        "role": "user",
                        "content": self.user_message,
                    }
                ]
            )
            generated_text = response.choices[0].message.content
            self.status = generated_text
            return Message(text=generated_text)
        except Exception as e:
            self.status = f"Error: {str(e)}"
            return Message(text=f"An error occurred: {str(e)}")

