from typing import Optional, Any
from axiestudio import CustomComponent
from axiestudio.schema import Record
from tempfile import NamedTemporaryFile
import subprocess
import sys
import base64
from json import loads


def install(package):
    subprocess.check_call([sys.executable, "-m", "pip", "install", package])
    return 1

install("requests")

class DiscordGetAudioMessage(CustomComponent):
    display_name = "DiscordGetAudioMessage"
    description = "Retrieve the audio message from the Discord channel."
    field_order = ["token", "channel_id", "limit"]
    icon = "Discord"

    def build_config(self) -> dict:
        return {
            "token": {
                "display_name": "Token",
                "advanced": False,
                "password": True,
                "required": True,
            }, "channel_id": {
                "display_name": "ChannelId",
                "advanced": False,
                "required": True,
            }, "limit": {
                "display_name": "Limit",
                "value": 20,
                "advanced": False,
                "info": "Number of messages that will be checked, the value 0 means limitless",
                "required": True,
            }
        }


    def build(self, token: str, channel_id: int,  limit: int) -> Record:
        import requests
        body = {"token": token, "limit": limit, "content_type": ["ogg"]}
        resp = requests.post(f"http://discord:7880/api/v1/channels/{channel_id}/get_messages/last", json=body)
        if resp.status_code == 200:
            file = loads(resp.content)
            tmp_file = NamedTemporaryFile(delete=False, suffix=".ogg")
            resolved_path = self.resolve_path(tmp_file.name)
            with open(tmp_file.name, 'wb') as f:  # Open the file in binary mode
                f.write(base64.b64decode(file["content"]))
            return Record(data={"file_path": resolved_path, "text": file["content"]})
        else:
            raise Exception("Timeout exception")

