from axiestudio.custom import Component
from axiestudio.io import Output
from langchain.tools import Tool
from importlib import metadata

class LangflowVersionToolComponent(Component):
    display_name = "Langflow Version Tool"
    description = "A tool that returns the current version of Langflow."
    icon = "ðŸ”§"

    inputs = []  # No inputs for the component itself

    outputs = [
        Output(display_name="Langflow Version Tool", name="axiestudio_version_tool", method="create_tool"),
    ]

    def get_version(self) -> str:
        try:
            version = metadata.version("axiestudio")
            return f"Current Langflow version: {version}"
        except Exception as e:
            return f"An error occurred while retrieving the version: {str(e)}"

    def create_tool(self) -> Tool:
        def version_function(_input: str = "") -> str:  # Aceitando um argumento
            return self.get_version()

        tool = Tool(
            name="LangflowVersionChecker",  # Nome da Tool ajustado
            func=version_function,
            description="Returns the current version of Langflow installed."
        )

        self.status = "Langflow version tool created successfully"
        return tool

