import json
import requests

from axiestudio.custom import Component
from axiestudio.io import MultilineInput, Output
from axiestudio.schema import Data


class WebhookComponent(Component):
    display_name = "Webhook Input"
    description = "Defines a webhook input for the flow."
    name = "Webhook"

    # Configurações da Evolution API
    EVOLUTION_API_URL = "https://evolutionapi.curitibadigital.online"  # Substitua pela URL real da Evolution API
    API_KEY = "lkng5kc6xodrrbbzpl9xus"  # Substitua pela sua chave de API real

    inputs = [
        MultilineInput(
            name="data",
            display_name="Data",
            info="Use this field to quickly test the webhook component by providing a JSON payload.",
        )
    ]
    outputs = [
        Output(display_name="Data", name="output_data", method="build_data"),
    ]

    def build_data(self) -> Data:
        message: str | Data = ""
        if not self.data:
            self.status = "No data provided."
            return Data(data={})
        try:
            body = json.loads(self.data or "{}")
        except json.JSONDecodeError:
            body = {"payload": self.data}
            message = f"Invalid JSON payload. Please check the format.\n\n{self.data}"
        
        # Envio dos dados para a Evolution API
        try:
            response = requests.post(
                self.EVOLUTION_API_URL,
                json=body,
                headers={"Authorization": f"Bearer {self.API_KEY}"}
            )

            if response.status_code == 200:
                api_response = response.json()
                message = f"Success: {api_response}"
            else:
                message = f"API Error: {response.status_code} - {response.text}"

        except Exception as e:
            message = f"Connection Error: {str(e)}"

        data = Data(data=body)
        if not message:
            message = data
        self.status = message
        return data

