from typing import cast
import requests
from google.auth.transport.requests import Request
from google.oauth2 import service_account
from axiestudio.base.models.model import LCModelComponent
from axiestudio.field_typing import LanguageModel
from axiestudio.inputs import MessageTextInput
from axiestudio.io import BoolInput, FileInput, FloatInput, IntInput, StrInput

class CustomVertexAIComponent(LCModelComponent):
    display_name = "Custom Vertex AI"
    description = "Generate text using custom Vertex AI endpoints."
    icon = "VertexAI"
    name = "VertexAiCustomModel"

    inputs = LCModelComponent._base_inputs + [
        FileInput(name="credentials", display_name="Credentials", info="Google service account JSON file", file_types=["json"]),
        StrInput(name="project", display_name="Project", info="The project ID.", advanced=True),
        StrInput(name="location", display_name="Location", value="us-central1", advanced=True),
        StrInput(name="endpoint_id", display_name="Endpoint ID", info="The custom endpoint ID"),
        IntInput(name="max_output_tokens", display_name="Max Output Tokens", advanced=True),
        IntInput(name="max_retries", display_name="Max Retries", value=1, advanced=True),
        FloatInput(name="temperature", value=0.0, display_name="Temperature"),
        IntInput(name="top_k", display_name="Top K", advanced=True),
        FloatInput(name="top_p", display_name="Top P", value=0.95, advanced=True),
        BoolInput(name="verbose", display_name="Verbose", value=False, advanced=True),
    ]

    def build_model(self) -> LanguageModel:
        project = self.project or "default_project"
        endpoint_id = self.endpoint_id or "default_endpoint"
        location = self.location or "us-central1"
        api_url = f"https://{location}-aiplatform.googleapis.com/v1/projects/{project}/locations/{location}/endpoints/{endpoint_id}:predict"

        # Use the JSON credentials from the service account key file
        if self.credentials:
            credentials = service_account.Credentials.from_service_account_file(self.credentials, scopes=["https://www.googleapis.com/auth/cloud-platform"])
            credentials.refresh(Request())  # Refresh to get a valid access token
            access_token = credentials.token
        else:
            raise ValueError("Service account credentials are required.")

        headers = {
            "Authorization": f"Bearer {access_token}",
            "Content-Type": "application/json"
        }

        def predict(input_text: str) -> str:
            # Ensure input is properly formatted
            input_text = str(input_text)  # Ensure it's treated as a string

            payload = {
                "instances": [{"prompt": input_text, "max_tokens": self.max_output_tokens}],  # Ensure it's a string
                "parameters": {
                    "temperature": self.temperature,
                    "topK": self.top_k,
                    "topP": self.top_p,
                    "maxOutputTokens": self.max_output_tokens,
                }
            }

            response = requests.post(api_url, headers=headers, json=payload)

            if response.status_code == 200:
                return response.json()
            else:
                raise ValueError(f"Error: {response.status_code} - {response.text}")

        class CustomVertexAIRunnable:
            def __init__(self, predict_fn):
                self.predict_fn = predict_fn

            def invoke(self, input_text: str) -> str:
                return self.predict_fn(input_text)

            def with_config(self, config, **kwargs):  # Accept extra argument config
                return self

        return cast(LanguageModel, CustomVertexAIRunnable(predict))

