from axiestudio.schema import Record


class StructureDefinition(CustomComponent):
    display_name: str = "Structure Definition"
    description: str = "整理 Structure Definition"
    documentation: str = "http://docs.axiestudio.org/components/custom"
    icon = "custom_components"

    def build_config(self):
        return {
            "param": {"display_name": "Parameter"},
            "records": {"display_name": "Records"},
        }

    def build(self, records: list[Record]) -> Record:
        return_records = []
        ids = []
        for record in records:
            data = record.data
            seq = data['ig_ele_seq']
            if seq not in ids:
                data['text'] = {
                    "ig_ele_seq": data["ig_ele_seq"],
            		"ig_element_path": data["ig_element_path"],
            		"ig_element_short_def": data["ig_element_short_def"]
            		}
                return_records.append(record)
                ids.append(seq)
        self.status = return_records
        return return_records

